<?phpclass ExhibitorTypeModel extends Model{	//自动验证	protected $_validate=array(		array('name','require','类型名称必须！',1,'',3),		array('name','','类型名称已经存在！',1,'unique',3), 		array('status','require','类型状态必须！',1,'',3),	);		public function delType($where){		if($where){			return $this->where($where)->delete();		}else{			return false;		}	}		public function getType($where = '',$field = '*') {		return $this->field($field)->where($where)->find();	}	}