<?phpclass ExhibitionTypeModel extends Model{	//自动验证	protected $_validate=array(		array('name','require','类型名称必须！',1,'',3),		array('name','','类型名称已经存在！',1,'unique',3), 		array('status','require','类型状态必须！',1,'',3),	);		// 获取所有展览类型信息	public function getAllType($where = '' , $order = 'sort DESC' ,$field = '*') {		return $this->field($field)->where($where)->order($order)->select();	}		public function delType($where){		if($where){			return $this->where($where)->delete();		}else{			return false;		}	}		public function getType($where = '',$field = '*') {		return $this->field($field)->where($where)->find();	}			// 更新类型	public function upType($data) {		if($data){			return $this->save($data);		}else{			return false;		}	}}